import Botao from "../Botao/botao";
import CampoTexto from "../CampoTexto";
import ListaSuspensa from "../ListaSuspensa";
import "./formulario.css";
import { useState} from 'react'

const Formulario = (props) => {
  const posicao = [
    "Goleiro",
    "Lateral-Direito",
    "Lateral-Esquerdo",
    "Zagueiro",
    "Volante",
    "Meia-Direita",
    "Meia-Esquerda",
    "Centroavante",
  ];

  const [nome, setNome] = useState("")
  const [posicaoSelecionada, setPosicaoSelecionada] = useState("")
  const [ano, setAno] = useState("")
  const [imagem, setImagem] = useState("")

  const aoSalvar = (evento) => {
    evento.preventDefault();
    props.aoJogadorCadastrado({
    nome,
    posicaoSelecionada,
    ano,
    imagem
    })
console.log(nome, posicaoSelecionada,ano.imagem)
    // setNome("")
    // setPosicaoSelecionada("")
    // setAno("")
    // setImagem("")
  };


  return (
    <section className="formulario">
      <form onSubmit={aoSalvar}>
        <h1>CADASTRO DO JOGADOR</h1>
        <CampoTexto
          label="NOME"
          obrigatorio={true}
          placeholder="Digite seu nome"
          aoAlterado= {valor => setNome(valor)}
        />
        <ListaSuspensa
          label="POSIÇÃO"
          itens={posicao}
          obrigatorio={true}
          placeholder="Digite a posição"
          aoAlterado= {valor => setPosicaoSelecionada(valor)}
        />
        <CampoTexto
          label="ANO"
          obrigatorio={true}
          placeholder="Digite o ano que jogou"
          aoAlterado= {valor => setAno(valor)}
        />
        <CampoTexto label="IMAGEM" 
        placeholder="Insira a imagem" 
        valor={imagem}
        aoAlterado= {valor => setImagem(valor)}
        />
        <Botao />
      </form>
    </section>
  );
};

export default Formulario;
