import "./formulario.css";
import { useState} from 'react'

const Formulario = (props) => {
  const posicao = [
    "Goleiro",
    "Lateral-Direito",
    "Lateral-Esquerdo",
    "Zagueiro",
    "Volante",
    "Meia-Direita",
    "Meia-Esquerda",
    "Centroavante",
  ];

  const [nome, setNome] = useState("")
  const [posicaoSelecionada, setPosicaoSelecionada] = useState("")
  const [ano, setAno] = useState("")
  const [imagem, setNImage] = useState("")

  const aoSalvar = (evento) => {
    evento.preventDefault();

    console.log("Formulario enviado");
  };

  return (
    <section className="formulario">
      <form>
        <h1>CADASTRO DO JOGADOR</h1>
        <CampoTexto
          label="NOME"
          obrigatorio={true}
          placeholder="Digite seu nome"
          aoAlterado= {valor => setNome}
        />
        <ListaSuspensa
          label="POSIÇÃO"
          itens={posicao}
          obrigatorio={true}
          placeholder="Digite a posição"
        />
        <CampoTexto
          label="ANO"
          obrigatorio={true}
          placeholder="Digite o ano que jogou"
        />
        <CampoTexto label="IMAGEM" placeholder="Insira a imagem" />
        <Botao />
      </form>
    </section>
  );
};

export default Formulario;
