import { useState } from "react";
import "./App.css";
import Banner from "./componentes/Banner";
import Formulario from "./componentes/Formulario";
import TimePorAno from "./componentes/TimePorAno";
import Rodape from "./componentes/Rodape";

function App() {
  const [jogadores, setJogadores] = useState([]);

  const [genero, setGenero] = useState("masculino");

  const aoNovoJogadorAdicionado = (jogador) => {
    console.log(jogador);
    setJogadores([...jogadores, jogador]);
  };

  const JogadoresPorAno = jogadores.reduce((grupo, jogador) => {
    if (!grupo[jogador.ano]) {
      grupo[jogador.ano] = [];
    }
    grupo[jogador.ano].push(jogador);
    return grupo;
  }, {});

  return (
    <div className="App">
      <Banner genero={genero} aoGeneroAlterado={setGenero} />
      <Formulario
        aoJogadorCadastrado={(jogador) => aoNovoJogadorAdicionado(jogador)} genero="masculino" aoSalvar={handleSalvar}
      />
      {Object.keys(JogadoresPorAno).map((ano) => (
        <TimePorAno key={ano} ano={ano} jogadores={JogadoresPorAno[ano]} />
      ))}
      <Rodape />
    </div>
  );
}

export default App;
