import "./timePorAno.css";
import Jogador from "../Jogador";

function getFundoDoAno(anos) {
  // Funções conforme definidas acima
  const { r, g, b } = corDeFundoPorAno(anos);
  const lightenFactor = 0.8;
  const newR = Math.round(r + (255 - r) * lightenFactor);
  const newG = Math.round(g + (255 - g) * lightenFactor);
  const newB = Math.round(b + (255 - b) * lightenFactor);
  return `rgb(${newR}, ${newG}, ${newB})`;
}

function corDeFundoPorAno(anos) {
  const anoInicio = 1910;
  const ultimoAno = 2025;
  const factor = Math.min(Math.max((anos - anoInicio) / (ultimoAno - anoInicio), 0), 1);
  const r = Math.round(192 + factor * (255 - 192));
  const g = Math.round(192 + factor * (255 - 192));
  const b = Math.round(192 + factor * (255 - 192));
  return { r, g, b };
}

const TimePorAno = (props) => {
  const fundoDoAno = getFundoDoAno(Number(anos));
  return (
    <section className="time">
      <h3>{props.ano}</h3>
      <div className="jogadores">
        {props.jogadores.map((jogador,index) => (
          <Jogador
            key={index}
            nome={jogador.nome}
            posicaoSelecionada={jogador.posicaoSelecionada}
            imagem={jogador.imagem}
            ano={jogador.ano}
          />
        ))}
      </div>
    </section>
  );
};

export default TimePorAno;
